import moment from "moment";

export const nasaApiDateFormat = (date) => {
  return moment(date).format("YYYY-MM-DD");
};

export const addDaysToDate = (date, days) => {
  return moment(date).add(days, "days");
};

export const checkDateIsAfterThresholdDate = (dateToCheck, thresholdDate) => {
  let startOfDateToCheck = new Date(dateToCheck).setHours(0, 0, 0, 0);
  let startOfThresholdDate = new Date(thresholdDate).setHours(0, 0, 0, 0);
  return moment(startOfDateToCheck).isAfter(startOfThresholdDate);
};
