import { useEffect, useState } from "react";
import axios from "axios";
import { nasaAPIDateFormat, addDaysToDate } from "../utils/dateUtils";

const requestParams = {
  url: `https://api.nasa.gov/planetary/apod?api_key=${
    process.env.REACT_APP_NASA_API_KEY
  }&start_date=${nasaAPIDateFormat(
    selectedDate
  )}&end_date=${dateFonasaAPIDateFormatmat(new Date())}`,
  method: "GET",
};

const useBatchedNasaImageQuery = (startDate, currBatch, batchSize) => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    setData([]);
  }, [startDate]);

  useEffect(() => {
    axios({
      method,
      url,
      data,
      headers,
    })
      .then((response) => {
        if (response.data && Array.isArray(response.data)) {
          setData(data.concat(response.data));
        } else if (response.data) {
          setData(data.concat([response.data]));
        }
      })
      .catch((error) => {
        setError(error);
      });
  });

  return [data, error];
};

export default useBatchedNasaImageQuery;
